# encoding=utf-8# 配置文件写入锁from Experiment.Test.ThreadTest.Global_Values import lock, dic_testfrom SDK.MyTimeOPT import get_current_datetime_strimport timeimport randomimport threadingdef callback_thread(lock, data_dic, name):    """    :param data_dic:    :param name:    :return:    """    while True:        if lock.acquire(timeout=0.5):            try:                print(name + '1')                data_dic.append(name + '数据 ' + get_current_datetime_str())                time.sleep(random.random()*5)            finally:                lock.release()                print(name + '0')        else:            print(name + ' 没有得到 锁！')if __name__ == '__main__':    slave_thread = threading.Thread(target=callback_thread, args=(lock, dic_test, '从程序'))    slave_thread.start()        callback_thread(lock, dic_test, '主程序')