# encoding=utf-8import wxclass Example(wx.Frame):    def __init__(self, parent, title):        super(Example, self).__init__(parent, title=title, size=(300, 200))        self.InitUI()        self.Centre()        self.Show()    def InitUI(self):        p = wx.Panel(self)        nrows, ncols = 100, 4        gs = wx.GridSizer(nrows, ncols, 5, 5)        for r in range(nrows):            for c in range(ncols):                _n = ncols * r + c                btn = "Btn" + str(_n)                gs.Add(wx.Button(p, label=btn), 0, wx.EXPAND)                p.SetSizer(gs)app = wx.App()Example(None, title='Grid Demo - www.yiibai.com')app.MainLoop()